import React from "react";
import Plot from "react-plotly.js";
import axios from 'axios';


class Graph extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
            data: [
                {
                x: [],
                y: [],
                type: 'bar',
                }],
            layout: {
                height: 500,
                width: 900,
                title: "Area chart",
                paper_bgcolor: 'linear-gradient(58deg,rgb(152, 205, 255) 0%,rgb(239, 199, 255) 80%)',
                plot_bgcolor: 'linear-gradient(58deg,rgb(152, 205, 255) 0%,rgb(239, 199, 255) 80%)',
                font: {family: "Arial",size: 18, color:"white"},
                xaxis: {range: [0, 50], zerolinecolor: "white", gridcolor: "white"},
                yaxis: {range: [0, 11], zerolinecolor: "white", gridcolor: "white"}
            },
            titles: [],
            years: [],
            age_ratings: [],
            imdb_ratings: [],
            on_netflixs: []
    };
  }

  componentDidMount() {
      axios.get('http://localhost:8000/api/')
      .then(res => {
          const nflix = res.data;
          const title = [];
          const year = [];
          const age_rating = [];
          const imdb_rating = [];
          const on_netflix = [];
          nflix.map((movie) => (
              title.push(movie.title),
              year.push(movie.year),
              age_rating.push(movie.age_rating),
              imdb_rating.push(movie.imdb_rating),
              on_netflix.push(movie.on_netflix)
          ));
        this.setState({data: [{x: title, y: imdb_rating}], titles: title, years: year, age_ratings: age_rating, imdb_ratings: imdb_rating, on_netflixs: on_netflix});
      })
  }
  render() {
    return (
      <div style={{ width: "100%", height: "100%" }}>
        <Plot
          data={this.state.data}
          layout={this.state.layout}
          onInitialized={(figure) => this.setState(figure)}
          onUpdate={(figure) => this.setState(figure)}
        />
      </div>
    );
  }
}
export default Graph;
