import React from 'react'
import Plot from "react-plotly.js";
import axios from 'axios';
import './style.css'

axios.defaults.xsrfCookieName = 'csrftoken'
axios.defaults.xsrfHeaderName = 'X-CSRFToken'


class Graph2 extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [
                {
                    type: 'scatter3d',
                    x: [],
                    y: [],
                    z: [],
                }],
            layout: {
                height: 500,
                width: 1200,
                paper_bgcolor: 'rgb(97, 92, 87)',
                plot_bgcolor: 'rgb(97, 92, 87)',
                font: { family: "Arial", size: "15", color: "rgb(253, 237, 165)" },
                xaxis: { range: [1990, 2021], zerolinecolor: "rgb(253, 237, 165)", title: "Release Year", scaleanchor: 'z', scaleratio: 10 },
                yaxis: { zerolinecolor: "rgb(253, 237, 165)", title: "Title" },
                zaxis: { range: [0, 10], zerolinecolor: "rgb(253, 237, 165)", title: "IMDB Rating" },
            },
            titles: [],
            years: [],
            age_ratings: [],
            imdb_ratings: [],
            on_netflixs: []
        };
    }

    componentDidMount() {
        axios.get('http://localhost:8000/api/')
            .then(res => {
                const nflix = res.data;
                const title = [];
                const year = [];
                const age_rating = [];
                const imdb_rating = [];
                const on_netflix = [];
                nflix.map((movie) => (
                    title.push(movie.title),
                    year.push(movie.year),
                    age_rating.push(movie.age_rating),
                    imdb_rating.push(movie.imdb_rating),
                    on_netflix.push(movie.on_netflix)
                ));
                this.setState({
                    data: [{
                        x: year.slice(0, 10),
                        y: title.slice(0, 10),
                        z: imdb_rating.slice(0, 10),
                        type: 'scatter3d',
                        marker: {
                            color: "rgb(255, 208, 0)"
                        }
                    }],
                    titles: title,
                    years: year,
                    age_ratings: age_rating,
                    imdb_ratings: imdb_rating,
                    on_netflixs: on_netflix
                }
                );
            })
    }
    render() {
        return (
            <section>
                <p className='pltttle2'>Neftlix IMDB Ratings</p>
                <div style={{ width: "100%", height: "100%" }}>
                    <Plot
                        data={this.state.data}
                        layout={this.state.layout}
                        onInitialized={(figure) => this.setState(figure)}
                        onUpdate={(figure) => this.setState(figure)}
                    />
                </div>
            </section>
        );
    }
}
export default Graph2;
