import React from 'react'
import './style.css'
import { useMediaQuery } from 'react-responsive'
import Card2 from '../../Components/Card2'

/**
* @author
* @function Interests
**/


const Interests = (props) => {
  const isDesktopOrLaptop = useMediaQuery({ query: '(min-width: 1224px)' })
  const isTabletOrMobile = useMediaQuery({ query: '(max-width: 1224px)' })
  const isPortrait = useMediaQuery({ query: '(orientation: portrait)' })
  const isLandscape = useMediaQuery({ query: '(orientation: landscape)' })

  return (
    <section className="Ihme">
      <Card2>
        <h1>This web app is responsive</h1>
        <h2>Change the size of your browser window to see how event listeners pick up your action and effect responsive change on the webpage. </h2>
        {isDesktopOrLaptop && <p>Desktop/Laptop Setting</p>}
        {isTabletOrMobile && <p>Phone/Tablet Setting</p>}
        {isPortrait && <p>You are in portrait mode</p>}
        {isLandscape && <p>You are in landscape mode</p>}
      </Card2>
    </section>
  )

}

export default Interests
