import React from 'react'
import './style.css'
import { useMediaQuery } from 'react-responsive'
import Card2 from '../../Components/Card2'

/**
* @author
* @function Interests
**/


const Interests = (props) => {
  const isDesktopOrLaptop = useMediaQuery({ query: '(min-width: 1224px)' })
  const isTabletOrMobile = useMediaQuery({ query: '(max-width: 1224px)' })
  const isPortrait = useMediaQuery({ query: '(orientation: portrait)' })
  const isLandscape = useMediaQuery({ query: '(orientation: landscape)' })

  return (
    <section className="Ihme">
      <div className='response'>
        <Card2>
          <h2>This web app is responsive</h2>
          <h1>Change the size of your browser window to see how event listeners pick up your action and effect responsive change on the webpage. </h1>
          {isDesktopOrLaptop && <p>Setting: Desktop/Laptop</p>}
          {isTabletOrMobile && <p>Setting: Phone/Tablet</p>}
          {isPortrait && <p>Mode: Portrait</p>}
          {isLandscape && <p>Mode: Landscape</p>}
        </Card2>
      </div>
    </section>
  )

}

export default Interests
