import React from "react";
import Plot from "react-plotly.js";
import axios from 'axios';


class Graph extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        figure: {
            data: [
                {
                x: [],
                y: [],
                fill: 'tozeroy',
                type: 'scatter',
                name: 'Vendor',
                marker: { color: "white"}
                }],
            layout: {
                height: 500,
                width: 900,
                title: "Area chart",
                paper_bgcolor: 'linear-gradient(58deg,rgb(152, 205, 255) 0%,rgb(239, 199, 255) 80%)',
                plot_bgcolor: 'linear-gradient(58deg,rgb(152, 205, 255) 0%,rgb(239, 199, 255) 80%)',
                font: {family: "Arial",size: 18, color:"white"},
                xaxis: {range: [0, 10], zerolinecolor: "white", gridcolor: "white"},
                yaxis: {range: [0, 10], zerolinecolor: "white", gridcolor: "white"}
            }
        },
        title: [],
        year: [],
        age_rating: [],
        imdb_rating: [],
        on_netflix: []
    };
  }

  componentDidMount() {
      axios.get('http://localhost:8000/api/')
      .then(res => {
          const nflix = res.data;
          const title = [];
          const year = [];
          const age_rating = [];
          const imdb_rating = [];
          const on_netflix = [];
          nflix.map((movie) => (
              title.push(movie.title),
              year.push(movie.year),
              age_rating.push(movie.age_rating),
              imdb_rating.push(movie.imdb_rating),
              on_netflix.push(movie.on_netflix)
          ));
        this.setState({figure: {Data: [{x: title , y: imdb_rating}]}, title: title, year: year, age_rating: age_rating, imdb_rating: imdb_rating, on_netflix: on_netflix});
      })
  }
  render() {
    return (
      <div style={{ width: "100%", height: "100%" }}>
        <Plot
          data={this.state.figure.data}
          layout={this.state.figure.layout}
          onInitialized={(f) => this.setState(f)}
          onUpdate={(f) => this.setState(f)}
        />
      </div>
    );
  }
}
export default Graph;
